import React, { Fragment, useState } from "react";
import { Link, withRouter } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { addExperince } from "../../actions/profile";

const AddExperience = props => {
  const [formData, setFormData] = useState({
    title: "",
    company: "",
    location: "",
    from: "",
    current: "",
    to: "",
    description: ""
  });

  const { title, company, location, from, current, to, description } = formData;
  return (
    <Fragment>
      <form className="form">
        <div className="form-group">
          <input type="text" placeholder="* Job Title" name="title" value={title} onClick={(e)=>onchange(e)} required />
        </div>
        <div className="form-group">
          <input type="text" placeholder="* Company" name="company" value={company} required />
        </div>
        <div className="form-group">
          <input type="text" placeholder="Location" name="location" value={location} />
        </div>
        <div className="form-group">
          <h4>From Date</h4>
          <input type="date" name="from" value={from} />
        </div>
        <div className="form-group">
          <p>
            <input type="checkbox" name="current" value={current} /> Current Job
          </p>
        </div>
        <div className="form-group">
          <h4>To Date</h4>
          <input type="date" name="to" value={to} />
        </div>
        <div className="form-group">
          <textarea
            name="description"
            cols="30"
            rows="5"
            placeholder="Job Description"
            value={description}
          ></textarea>
        </div>
        <input type="submit" className="btn btn-primary my-1" />
        <Link className="btn btn-light my-1" to="/dashboard">
          Go Back
        </Link>
      </form>
    </Fragment>
  );
};

AddExperience.propTypes = {
  addExperience: PropTypes.func.isRequired
};

export default connect(
  null,
  { addExperince }
)(AddExperience);
